def longest_palindrome(s):
    dic = {}
    ans = 0
    odd_char_found = 0
    for c in s:
        dic[c] = dic.get(c, 0) + 1

    for key in dic:
        if dic[key] % 2 == 0:
            ans += dic[key]
        else:
            ans += dic[key] - 1
            odd_char_found = 1

    ans += odd_char_found

    return ans

